mapper.select:
/* ProgramID=com.main.admin.menu.service.impl.ProductMapper.selectPaging */ SELECT T.* FROM (SELECT A.* FROM (
SELECT seq, name, pcode, kr_ck, en_ck, jp_ck, (SELECT name FROM tb_code_mgt WHERE code = tb_product.kr_code AND useyn = 'Y') AS kr_code, (SELECT name FROM tb_code_mgt WHERE code = tb_product.en_code AND useyn = 'Y') AS en_code, (SELECT name FROM tb_code_mgt WHERE code = tb_product.jp_code AND useyn = 'Y') AS jp_code, kr_name, en_name, jp_name, s_price, l_price, c_price, kr_menu, en_menu, jp_menu, best_ck, new_ck, hot_ck, f_file1, regid, regdt, useyn, modid, moddt, c_yn FROM tb_product
1 = 1
ORDER BY seq DESC ) A ) T LIMIT #{startCount}, #{pageUnit}

